import React, { useState } from 'react';
import { DES, enc } from 'crypto-js';

const App = () => {
  const [message, setMessage] = useState('');
  const [encryptedMessage, setEncryptedMessage] = useState('');
  const [decryptedMessage, setDecryptedMessage] = useState('');
  const [key, setKey] = useState('');

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();
    reader.onload = (e) => {
      const fileContent = e.target.result;
      setMessage(fileContent);
      setEncryptedMessage('');
      setDecryptedMessage('');
      setKey('');
    };
    reader.readAsText(file);
  };

  const encryptMessage = () => {
    if (message && key.length === 8) {
      const encrypted = DES.encrypt(message, key).toString();
      setEncryptedMessage(encrypted);
      setMessage('');
      setDecryptedMessage('');
      setKey('');
    }
  };

  const decryptMessage = () => {
    if (message && key.length === 8) {
      const decrypted = DES.decrypt(encryptedMessage, key).toString(enc.Utf8);
      setDecryptedMessage(decrypted);
      setMessage('');
      setKey('');
    }
  };

  const saveDecryptedMessage = () => {
    const blob = new Blob([decryptedMessage], { type: 'text/plain' });
    const downloadLink = document.createElement('a');
    downloadLink.href = URL.createObjectURL(blob);
    downloadLink.download = 'decrypted_message.txt';
    downloadLink.click();
  };

  return (
    <div className="bg-teal-900 text-white min-h-screen flex flex-col items-center justify-center">
      <h2 className="text-2xl mb-4">Cifrador y Descifrador DES</h2>
      <div className="flex flex-col items-center">
        <input
          type="file"
          accept=".txt"
          name="Archivo"
          onChange={(event) => handleFileUpload(event)}
          className="rounded py-2 px-4 mb-4"
        />
        <input
          type="text"
          placeholder="Clave (8 caracteres)"
          value={key}
          onChange={(e) => setKey(e.target.value)}
          className="rounded py-2 px-4 mb-4 text-black"
          maxLength={8}
        />
        <div>
          <button onClick={encryptMessage} className="rounded py-2 px-4 mb-4 mr-4">
            Cifrar
          </button>
          <button onClick={decryptMessage} className="rounded py-2 px-4 mb-4">
            Descifrar
          </button>
        </div>
        {encryptedMessage && (
          <div className="flex flex-col items-center">
            <button
              onClick={saveDecryptedMessage}
              className="rounded py-2 px-4 bg-blue-500 text-white"
            >
              Guardar archivo cifrado
            </button>
            <textarea
              value={encryptedMessage}
              readOnly
              className="rounded py-2 px-4 mb-4 text-black"
              rows={6}
              cols={40}
            />
          </div>
        )}
        {decryptedMessage && (
          <div className="flex flex-col items-center">
            <button
              onClick={saveDecryptedMessage}
              className="rounded py-2 px-4 bg-blue-500 text-white"
            >
              Guardar archivo descifrado
            </button>
            <textarea
              value={decryptedMessage}
              readOnly
              className="rounded py-2 px-4 mb-4 text-black"
              rows={6}
              cols={40}
            />
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
