import React, { useState } from 'react';
import { DES, enc } from 'crypto-js';
const App = () => {
  const [message, setMessage] = useState('');
  const [encryptedMessage, setEncryptedMessage] = useState('');
  const [decryptedMessage, setDecryptedMessage] = useState('');
  const [key, setKey] = useState('');

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();
    reader.onload = (e) => {
      setMessage(e.target.result);
    };
    reader.readAsText(file);
  };

  // Importa la librería crypto-js para utilizar el algoritmo DES


  const encryptMessage = () => {
    if (message && key.length === 8) {
      const encrypted = DES.encrypt(message, key).toString();
      setEncryptedMessage(encrypted);
    }
  };

  const decryptMessage = () => {
    if (encryptedMessage && key.length === 8) {
      const decrypted = DES.decrypt(encryptedMessage, key).toString(enc.Utf8);
      setDecryptedMessage(decrypted);
    }
  };

  return (
    <div className="bg-white-smoke min-h-screen flex flex-col items-center justify-center">
      <h2 className="text-2xl mb-4">Cifrador y Descifrador</h2>
      <div className="flex flex-col items-center">
        <input
          type="file"
          accept=".txt"
          onChange={handleFileUpload}
          className="bg-navy text-white rounded py-2 px-4 mb-4"
        />
        <input
          type="text"
          placeholder="Clave (8 caracteres)"
          value={key}
          onChange={(e) => setKey(e.target.value)}
          className="bg-navy text-white rounded py-2 px-4 mb-4"
          maxLength={8}
        />
        <button
          onClick={encryptMessage}
          className="bg-navy text-white rounded py-2 px-4 mb-4"
        >
          Cifrar
        </button>
        <button
          onClick={decryptMessage}
          className="bg-navy text-white rounded py-2 px-4 mb-4"
        >
          Descifrar
        </button>
        {encryptedMessage && (
          <textarea
            value={encryptedMessage}
            readOnly
            className="bg-navy text-white rounded py-2 px-4 mb-4"
          />
        )}
        {decryptedMessage && (
          <textarea
            value={decryptedMessage}
            readOnly
            className="bg-navy text-white rounded py-2 px-4 mb-4"
          />
        )}
      </div>
    </div>
  );
};

export default App;
