      setErrorMessage('¡La clave debe tener exactamente 8 caracteres!');
    } else {
      setErrorMessage('¡Ingresa un mensaje cifrado antes de descifrar!');
    }
  };

  return (
    <div className="bg-sky-blue min-h-screen flex justify-center items-center text-white">
      <div className="w-1/2">
        <h2 className="text-2xl font-bold mb-4 text-center">Cifrado y Descifrado</h2>
        <div className="mb-4">
          <label htmlFor="message" className="block mb-1">
            Mensaje:
          </label>
          <input
            type="text"
            id="message"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            className="w-full px-3 py-2 bg-transparent border border-gray-300 rounded focus:outline-none focus:border-blue-500"
          />
        </div>
        <div className="mb-4">
          <label htmlFor="key" className="block mb-1">
            Clave (8 caracteres):
          </label>
          <input
            type="text"
            id="key"
            value={key}
            onChange={(e) => setKey(e.target.value)}
            className="w-full px-3 py-2 bg-transparent border border-gray-300 rounded focus:outline-none focus:border-blue-500"
          />
        </div>
        <div className="mb-4 flex justify-center">
          <button
            onClick={handleEncrypt}
            className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 mr-4"
          >
            Cifrar
          </button>
          <button
            onClick={handleDecrypt}
            className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
          >
            Descifrar
          </button>
