        {encryptedMessage && (
          <div className="mb-4">
            <h3 className="text-lg font-bold">Mensaje Cifrado:</h3>
            <p>{encryptedMessage}</p>
          </div>
        )}
        {decryptedMessage && (
          <div>
            <h3 className="text-lg font-bold">Mensaje Descifrado:</h3>
            <p>{decryptedMessage}</p>
          </div>
        )}
      </div>
    </div>
  );
};


export default App;
