import React, { useState } from 'react';
import { DES, enc } from 'crypto-js';

const App = () => {
  const [message, setMessage] = useState('');
  const [key, setKey] = useState('');
  const [encryptedMessage, setEncryptedMessage] = useState('');
  const [decryptedMessage, setDecryptedMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleEncrypt = () => {
    if (message && key.length === 8) {
      setErrorMessage('');
      setEncryptedMessage('');

      const encryptMessage = () => {
        if (message && key.length === 8) {
          const encrypted = DES.encrypt(message, key).toString();
          setEncryptedMessage(encrypted);
        }
      };

      // Limpia el campo de archivo cargado y la clave ingresada
      setMessage('');
      setKey('');
    } else if (key.length !== 8) {
      setErrorMessage('¡La clave debe tener exactamente 8 caracteres!');
    } else {
      setErrorMessage('¡Ingresa un mensaje antes de cifrar!');
    }
  };

  const handleDecrypt = () => {
    if (encryptedMessage && key.length === 8) {
      setErrorMessage('');
      setDecryptedMessage('');

      const decryptMessage = () => {
        if (encryptedMessage && key.length === 8) {
          const decrypted = DES.decrypt(encryptedMessage, key).toString(enc.Utf8);
          setDecryptedMessage(decrypted);
        }
      };

      // Limpia la clave ingresada
      setKey('');
    } else if (key.length !== 8) {
      setErrorMessage('¡La clave debe tener exactamente 8 caracteres!');
    } else {
      setErrorMessage('¡Ingresa un mensaje cifrado antes de descifrar!');
    }
  };

  return (
    <div className="container mx-auto py-8">
      <h2 className="text-2xl font-bold mb-4">Cifrado y Descifrado</h2>
      <div className="mb-4">
        <label htmlFor="message" className="block mb-1">
          Mensaje:
        </label>
        <input
          type="text"
          id="message"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          className="w-full px-3 py-2 border border-gray-300 rounded focus:outline-none focus:border-blue-500"
        />
      </div>
      <div className="mb-4">
        <label htmlFor="key" className="block mb-1">
          Clave (8 caracteres):
        </label>
        <input
          type="text"
          id="key"
          value={key}
          onChange={(e) => setKey(e.target.value)}
          className="w-full px-3 py-2 border border-gray-300 rounded focus:outline-none focus:border-blue-500"
        />
      </div>
      <div className="mb-4">
        <button
          onClick={handleEncrypt}
          className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
        >
          Cifrar
        </button>
        <button
          onClick={handleDecrypt}
          className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 ml-4"
        >
          Descifrar
        </button>
      </div>
      {errorMessage && <p className="text-red-500">{errorMessage}</p>}
      {encryptedMessage && (
        <div className="mb-4">
          <h3 className="text-lg font-bold">Mensaje Cifrado:</h3>
          <p>{encryptedMessage}</p>
        </div>
      )}
      {decryptedMessage && (
        <div>
          <h3 className="text-lg font-bold">Mensaje Descifrado:</h3>
          <p>{decryptedMessage}</p>
        </div>
      )}
    </div>
  );
};

export default App;
