import React, { useState } from 'react';
import { DES, enc } from 'crypto-js';

const App = () => {
  const [message, setMessage] = useState('');
  const [encryptedMessage, setEncryptedMessage] = useState('');
  const [decryptedMessage, setDecryptedMessage] = useState('');
  const [key, setKey] = useState('');

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();
    reader.onload = (e) => {
      setMessage(e.target.result);
    };
    reader.readAsText(file);
  };

  const encryptMessage = () => {
    if (message && key.length === 8) {
      const encrypted = DES.encrypt(message, key).toString();
      setEncryptedMessage(encrypted);
      setMessage('');
      setKey('');
    }
  };

  const decryptMessage = () => {
    if (encryptedMessage && key.length === 8) {
      const decrypted = DES.decrypt(encryptedMessage, key).toString(enc.Utf8);
      setDecryptedMessage(decrypted);
    }
  };

  const saveEncryptedMessage = () => {
    const blob = new Blob([encryptedMessage], { type: 'text/plain' });
    const downloadLink = document.createElement('a');
    downloadLink.href = URL.createObjectURL(blob);
    downloadLink.download = 'encrypted_message.txt';
    downloadLink.click();
  };

  return (
    <div className="bg-teal-900 text-white min-h-screen flex flex-col items-center justify-center">
      <h2 className="text-2xl mb-4">Cifrador y Descifrador DES</h2>
      <div className="flex flex-col items-center">
        <input
          type="file"
          accept=".txt"
          name="Archivo"
          onChange={handleFileUpload}
          className="rounded py-2 px-4 mb-4"
        />
        <input
          type="text"
          placeholder="Clave (8 caracteres)"
          value={key}
          onChange={(e) => setKey(e.target.value)}
          className="rounded py-2 px-4 mb-4"
          maxLength={8}
        />
        <button onClick={encryptMessage} className="rounded py-2 px-4 mb-4">
          Cifrar
        </button>
        <button onClick={decryptMessage} className="rounded py-2 px-4 mb-4">
          Descifrar
        </button>
        {encryptedMessage && (
          <div className="flex flex-col items-center">
            <input
              type="text"
              value={key}
              onChange={(e) => setKey(e.target.value)}
              placeholder="Clave (8 caracteres)"
              className="rounded py-2 px-4 mb-4 text-black	"
              maxLength={8}
            />
            <button
              onClick={saveEncryptedMessage}
              className="rounded py-2 px-4 bg-blue-500 text-white"
            >
              Guardar archivo cifrado
            </button>
          </div>
        )}
        {decryptedMessage && (
          <textarea
            value={decryptedMessage}
            readOnly
            className="rounded py-2 px-4 mb-4"
            rows={6}
            cols={40}
          />
        )}
      </div>
    </div>
  );
};

export default App;
