import { DES, enc } from 'crypto-js';
import React, { useState } from 'react';

const App = () => {
  const [message, setMessage] = useState('');
  const [key, setKey] = useState('');
  const [encryptedMessage, setEncryptedMessage] = useState('');
  const [decryptedMessage, setDecryptedMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleEncrypt = () => {
    if (message && key.length === 8) {
      setErrorMessage('');
      setEncryptedMessage('');

      // Lógica de cifrado aquí

      // Limpia el campo de archivo cargado y la clave ingresada
      setMessage('');
      setKey('');
    } else if (key.length !== 8) {
      setErrorMessage('¡La clave debe tener exactamente 8 caracteres!');
    } else {
      setErrorMessage('¡Ingresa un mensaje antes de cifrar!');
    }
  };

  const handleDecrypt = () => {
    if (encryptedMessage && key.length === 8) {
      setErrorMessage('');
      setDecryptedMessage('');

      // Lógica de descifrado aquí

      // Limpia la clave ingresada
      setKey('');
    } else if (key.length !== 8) {
      setErrorMessage('¡La clave debe tener exactamente 8 caracteres!');
    } else {
      setErrorMessage('¡Ingresa un mensaje cifrado antes de descifrar!');
    }
  };

  return (
    <div className="bg-blue min-h-screen flex flex-col items-center justify-center">
      <h2 className="text-2xl mb-4">Cifrador y Descifrador</h2>
      <div className="flex flex-col items-center">
        <input
          type="file"
          accept=".txt"
          onChange={handleFileUpload}
          className="bg-navy  rounded py-2 px-4 mb-4"
        />
        <input
          type="text"
          placeholder="Clave (8 caracteres)"
          value={key}
          onChange={(e) => setKey(e.target.value)}
          className="bg-navy  rounded py-2 px-4 mb-4"
          maxLength={8}
        />
        <button
          onClick={encryptedMessage}
          className="bg-navy  rounded py-2 px-4 mb-4"
        >
          Cifrar
        </button>
        <button
          onClick={decryptedMessage}
          className="bg-navy  rounded py-2 px-4 mb-4"
        >
          Descifrar
        </button>
        {encryptedMessage && (
          <textarea
            value={encryptedMessage}
            readOnly
            className="bg-navy  rounded py-2 px-4 mb-4"
          />
        )}
        {decryptedMessage && (
          <textarea
            value={decryptedMessage}
            readOnly
            className="bg-navy  rounded py-2 px-4 mb-4"
          />
        )}
      </div>
    </div>
  );
};

export default App;
